import React from 'react';
import { StyleSheet } from 'react-native';

const globalStyles=StyleSheet.create({
    container: {
        backgroundColor: '#eef1e1',
        flex: 1
    },
    text: {
        color: '#333',
        fontSize: 12,
        fontFamily: 'RowdiesRegular'
    }
})

export default globalStyles;
